def build_poly_list(A, B, C, D, X):
    poly_list = []
    n = len(A)
    for i in range(n-1):
        def s(x):
            return A[i] + B[i]*(x-X[i]) + C[i]*((x-X[i])**2) + D[i]*((x-X[i])**3)
        poly_list.append(s)
    return poly_list
